	Color F2C(UInt16 value)
	{
		byte[] bytes = BitConverter.GetBytes( value );

		float R = (float)bytes[0];
		float G = (float)bytes[1];
		return new Color( R/255.0f, G/255.0f, 0.0f, 0.0f );
	}
